import imagefraise from './image/imagefraise.png'
import iconstockok from './image/iconstockok.png'
import iconstocknonok from './image/iconstocknonok.png'

function Carte() {
    const mymargin = {
        marginRight:'25px'
    }

    const stylecarte = {
        paddingRight:'10px',
        paddingLeft:'10px'
    }
    
    const imgStyle = {
        width: '100%', // Rendre l'image responsive en spécifiant la largeur à 100%
        height: 'auto' // Ajuster automatiquement la hauteur pour conserver les proportions de l'image
      };

    const divcarte = {
        width:'200px',

    }

    return (
        <div className='d-flex flex-column justify-content-center' style={{width:'200px'}}>
            <div style={{textAlign:'center'}}><img src={imagefraise} alt='image de fraise' style={imgStyle}/></div>
            <div style={{textAlign:'center',}}>Fraise</div>
            <div style={stylecarte}>
                <div className='d-flex flex-row justify-content-between'>
                    <div style={mymargin}>En stock</div>
                    <div><img src={iconstockok} alt='icon de stock ok'/></div>
                </div>
                <div className='d-flex flex-row justify-content-between'>
                    <div style={mymargin}>Unité</div>
                    <div>4</div>
                </div>
                <div className='d-flex flex-row justify-content-between'>
                    <div>Quantité</div>
                    <div>12 kg</div>
                </div>
                <div style={{textAlign:'right'}}>Détails</div>
            </div>
        </div>
    )
}

export default Carte
