import { Outlet, Link, useLocation } from "react-router-dom";
import logo from './image/logo_agroman.png';

import iconentrees from './image/iconentrees.png';
import iconsorties from './image/iconsorties.png';
import iconmodification from './image/iconmodification.png';
import iconutilisateurs from './image/iconutilisateurs.png';
import iconhistorique from './image/iconhistorique.png';
import iconproduits from "./image/iconproduits.png";

const Menu = () => {
    const mylink = {
        textDecoration: 'none', 
        color: 'inherit',
     
      };
    const myul = {
        listStyle: 'none', 
        padding: '0px' 
    }

    const location = useLocation()

  return (
    <div className="d-flex flex-row justify-content-start">
        <div className="col-sm-2 d-flex flex-row justify-content-center">
            <div>
                <div className="mb-3">
                    <img src={logo} alt="logo d'argoman"/>
                </div>
                <div className="d-flex flex-row justify-content-center">
                    <div>
                        <div className="mb-3" >
                            <img src={iconproduits} alt="icon produits"/>
                            <Link to="/" style={mylink}>Produits</Link>
                        </div>
                        <div className="mb-3">
                            <img src={iconentrees} alt="icon entree"/>
                            <Link to="/entrees" style={mylink}>Entrées</Link>
                        </div>
                        <div className="mb-3">
                            <img src={iconsorties} alt="icon sorties"/>
                            <Link to="/sorties" style={mylink}>Sorties</Link>
                        </div>
                        <div className="mb-3">
                            <img src={iconhistorique} alt="icon historique"/>
                            <Link to="/historique" style={mylink}>Historique</Link>
                        </div>
                        <div className="mb-3">
                            <img src={iconmodification} alt="icon modification"/>
                            <Link to="/modification" style={mylink}>Modification</Link>
                        </div>
                        <div className="mb-3">
                            <img src={iconutilisateurs} alt="icon utilisateur"/>
                            <Link to="/utilisateurs" style={mylink}>Utilisateurs</Link>
                        </div>
                    </div>
                </div>
            </div>

        </div>

        <div className="col-sm-8">
             <Outlet />
        </div>
        <div className="col-sm-2">

        </div>

    </div>
  )
};

export default Menu;
